function calcAdditionalPrice(){var e=document.getElementById("plans").value,t=document.getElementById("post"),n=document.getElementById("auto_reply_reviews"),l=document.getElementById("report"),a=document.getElementById("license");if(t.checked)if(e<14e4)var i=25e3;else if(14e4<=e<=23e4)i=5e4;else i=75e3;else i=0;if(n.checked)if(e<14e4)var d=2e4;else if(14e4<=e<=23e4)d=4e4;else d=55e3;else d=0;if(l.checked)if(e<14e4)var c=25e3;else if(14e4<=e<=23e4)c=5e4;else c=75e3;else c=0;if(a.checked)var r=5e3;else r=0;return parseInt(i)+parseInt(d)+parseInt(c)+parseInt(r)}$(document).on("turbolinks:load",function(){$(".price.rental").change(function(){var e=document.getElementById("plans").value,t=calcAdditionalPrice(),n=parseInt(e)+parseInt(t),l=n+5e4;document.getElementById("plan-value").innerText=e,document.getElementById("additional-value").innerText=t,document.getElementById("total-value").innerText=n,document.getElementById("total-init-value").innerText=l}),$(".addition .rental").change(function(){var e=calcAdditionalPrice();$("#additional-value")[0].innerText=e})});